import React from 'react';

import styles from '../css/addPost.module.css'

class AddPosts extends React.Component {
    constructor(props) {
        super(props)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleInputChange = this.handleInputChange.bind(this)
        this.state = {
            title: '',
            content: '',
            errorMessage: "",
        }
    }

    handleSubmit() {
        const token = localStorage.getItem('bearerToken')
        fetch(process.env.REACT_APP_WP_URL + '/wp/wp-json/wp/v2/posts/', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
                'Authorization': 'Bearer ' + token,
            },
            body: JSON.stringify({
                'title': this.state.title,
                'content': this.state.content,
                'status': 'publish'
            }),
        }).then((response) => {
            return response.json();
        }).then((data) => {
            this.checkResponse(data)
        });
    }

    checkResponse = (data) => {
        if(!data.code) {

            this.setState({
                errorMessage: ""
            })
            window.location.assign(process.env.REACT_APP_REACT_URL + '/'); 
        } else {
            this.setState({
                errorMessage: "Something went wrong.. Are you logged in?"
            })
        }
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });
      }

    render() {
        return (
            <div>
                <h1>Add a post</h1>
                <div className={styles.formContainer} >
                    <form method="post">
                        <div className={styles.inputContainer}>
                            <label className={styles.label}>Title</label>
                            <input
                                className={styles.input} 
                                name="title"
                                type="text"
                                value={this.state.username}
                                onChange={this.handleInputChange} />
                        </div>
                        <div className={styles.inputContainer}>
                            <label className={styles.label} for="content">Content</label>
                            <textarea
                                className={styles.textarea} 
                                name="content"
                                value={this.state.password}
                                onChange={this.handleInputChange} />
                        </div>
                    </form>
                    <button className={styles.button} type='Submit' onClick={this.handleSubmit}>Publish</button>
                    <p>{this.state.errorMessage}</p>
                </div>
            </div>
        )
    }
}


export default AddPosts
