import React from 'react';

import styles from '../css/login.module.css'

class AddPosts extends React.Component {
    constructor(props) {
        super(props)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleInputChange = this.handleInputChange.bind(this)
        this.state = {
            username: '',
            password: '',
            errorMessage: "",
        }
    }

    handleSubmit() {
        fetch(process.env.REACT_APP_WP_URL + '/wp/wp-json/jwt-auth/v1/token', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            body: JSON.stringify({
                'username': this.state.username,
                'password': this.state.password,
            }),
        }).then((response) => {
            return response.json();
        }).then((data) => {
            this.checkResponse(data)
        });
    }

    checkResponse = (data) => {
        if(data.token) {
            localStorage.setItem('bearerToken', data.token)
            this.setState({
                errorMessage: ""
            })
            window.location.assign(process.env.REACT_APP_REACT_URL + '/'); 
        } else {
            this.setState({
                errorMessage: "Wrong credentials."
            })
        }
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });
      }

    render() {
        return (
            <div>
                <h1>Add a post</h1>
                <div className={styles.formContainer} >
                    <form method="post">
                        <div className={styles.inputContainer}>
                            <label className={styles.label} for="title">Title</label>
                            <input
                                className={styles.input} 
                                name="title"
                                type="text"
                                value={this.state.username}
                                onChange={this.handleInputChange} />
                        </div>
                        <div className={styles.inputContainer}>
                            <label className={styles.label} for="content">Content</label>
                            <textarea
                                className={styles.input} 
                                name="content"
                                value={this.state.password}
                                onChange={this.handleInputChange} />
                        </div>
                    </form>
                    <button className={styles.button} type='Submit' onClick={this.handleSubmit}>Login</button>
                    <p>{this.state.errorMessage}</p>
                </div>
            </div>
        )
    }
}


export default AddPosts
